function requestData(url) {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      console.log(url), resolve(url);
    }, 2e3);
  });
}
function* request_G() {
  const res1 = yield requestData("zhangsan"),
    res2 = yield requestData(res1 + "aaa"),
    res3 = yield requestData(res2 + "bbb"),
    res4 = yield requestData(res3 + "ccc"),
    res5 = yield requestData(res4 + "ddd");
  console.log(res5);
}
function exec(genFn) {
  const interator = genFn();
  function exec(res) {
    const result = interator.next(res);
    if (result.done) return result.value;
    result.value.then((res) => {
      exec(res);
    });
  }
  exec();
}
exec(request_G);
